mapper.select:
SELECT
pl.*,
u.user_name AS userName,
s.name AS supplierName
FROM t_purchase_list pl
LEFT JOIN t_user u ON pl.user_id = u.id
LEFT JOIN t_supplier s ON pl.supplier_id = s.id


        pl.purchase_number = #{purchaseListQuery.purchaseNumber}


        and pl.supplier_id = #{purchaseListQuery.supplierId}


        and pl.state = #{purchaseListQuery.state}


        and pl.purchase_date >=#{purchaseListQuery.startDate}


        and pl.purchase_date <=#{purchaseListQuery.endDate}

SELECT tpl.purchase_number AS number, tpl.purchase_date AS date, ts.`name` AS `name`, plg.`code`, plg.`name` AS goodsName, plg.model, plg.unit AS unitName, plg.price, plg.num, plg.total, gt.`name` AS typeName FROM t_purchase_list AS tpl LEFT JOIN t_supplier AS ts ON tpl.supplier_id = ts.id LEFT JOIN t_purchase_list_goods AS plg ON tpl.id = plg.purchase_list_id LEFT JOIN t_goods_type AS gt ON plg.type_id = gt.id
and purchase_date >= #{purchaseListQuery.startDate}
and purchase_date <= #{purchaseListQuery.endDate}
and ( plg.name like concat('%',#{purchaseListQuery.goodsName},'%') or plg.code =#{purchaseListQuery.goodsName} )
and plg.type_id in
#{typeId}
LIMIT #{purchaseListQuery.index},#{purchaseListQuery.limit}
