mapper.select:
select
sl.*,
u.user_name as userName,
c.name as customerName
from
t_sale_list sl
left join t_user u on sl.user_id = u.id
left join t_customer c on sl.customer_id = c.id


        sl.sale_number = #{saleListQuery.saleNumber}


        and sl.customer_id = #{saleListQuery.customerId}


        and sl.state = #{saleListQuery.state}


        and sale_date >= #{saleListQuery.startDate}


        and sale_date <= #{saleListQuery.endDate}

select
count(*)
from
t_sale_list as tsl
left join t_customer as tc on tsl.customer_id = tc.id
left join t_sale_list_goods as tslt on tsl.id = tslt.sale_list_id
left join t_goods_type as tgt on tslt.type_id = tgt.id


        and sale_date >=#{saleListQuery.startDate}


        and sale_date <=#{saleListQuery.endDate}


        and
        (
        tslt.name like concat('%',#{saleListQuery.goodsName},'%') or tslt.code =#{saleListQuery.goodsName}
        )


        and tslt.type_id in

            #{typeId}

SELECT sl.sale_number as number, sl.sale_date as date, tc.`name` as name, tpl.`code`, tpl.`name` as goodsName, tpl.model, tpl.unit as unitName, tpl.price, tpl.num, tpl.total, tgt.`name` as typeName FROM t_sale_list AS sl LEFT JOIN t_customer AS tc ON sl.customer_id = tc.id LEFT JOIN t_sale_list_goods AS tpl ON sl.id = tpl.sale_list_id LEFT JOIN t_goods_type AS tgt ON tpl.type_id = tgt.id
and sale_date >= #{saleListQuery.startDate}
and sale_date <= #{saleListQuery.endDate}
and ( tpl.name like concat('%',#{saleListQuery.goodsName},'%') or tpl.code =#{saleListQuery.goodsName} )
and tpl.type_id in
#{typeId}
LIMIT #{saleListQuery.index},#{saleListQuery.limit}
